package practice;

public class MultipleIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age=3;
		if (age==18)
		{
			System.out.println("pass");
		}
		if(age>=20)
		{
			System.out.println("true");
		}
		else if(age<=18)
		{
			System.out.println("false");
		}
		//else()
		//{
			
		//}
		
	}

}
